const time=document.querySelector("#time"),prices=document.querySelectorAll(".price"),sliderThumb=document.querySelector(".slider-thumb"),basePrices=[19.99,24.99,39.99],setPrices=(733<window.innerHeight&&(document.body.style.height=window.innerHeight+"px"),t=>{console.log(t),prices.forEach((e,i)=>{e.textContent=""+(!0===t?(10*basePrices[i]+.09).toFixed(2):basePrices[i])})});time.addEventListener("click",()=>{sliderThumb.classList.toggle("slider-thumb--active");var e=sliderThumb.classList.contains("slider-thumb--active");setPrices(e)}),window.addEventListener("resize",()=>{733<window.innerHeight&&(document.body.style.height=window.innerHeight+"px")});